<script>
  let svgs = ["heart-svg", "php-svg", "codei-svg", "mysql-svg"]; // Agrega el ID de tu tercer SVG
  let currentIndex = 0;

  function toggleSVG() {
    // Ocultar todos los SVG
    svgs.forEach(id => document.getElementById(id).style.display = "none");

    // Mostrar el siguiente en la lista
    currentIndex = (currentIndex + 1) % svgs.length;
    document.getElementById(svgs[currentIndex]).style.display = "block";
  }

  // Cambia el SVG cada 2 segundos
  setInterval(toggleSVG, 2000);
</script>
